func TestVectors(t *testing.T) {
	for i, input := range inputs {
		local := Compute([]byte(input))
		lib := sha256.Sum256([]byte(input))

		if bytes.Compare(local[:], lib[:]) != 0 {
			t.Errorf("sha256.Compute: wrong result for `%d`, local = `0x%s`, lib = `0x%s`", i, common.Bytes2Hex(local[:]), common.Bytes2Hex(lib[:]))
		}
	}
}
